import React, { createContext, useContext, useState } from 'react';

const FornecedorContext = createContext();

export function useFornecedor() {
  return useContext(FornecedorContext);
}

export function FornecedorProvider({ children }) {
  const [fornecedorId, setFornecedorId] = useState(null);

  const selecionarFornecedor = (id) => {
    setFornecedorId(id);
  };

  return (
    <FornecedorContext.Provider value={{ fornecedorId, selecionarFornecedor }}>
      {children}
    </FornecedorContext.Provider>
  );
}
