import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal'; // Importe o react-modal

function AdicionarFornecedor({ onAdicionar }) {
  const [novoFornecedor, setNovoFornecedor] = useState({
    nome: '',
    descricao: '',
    categoria: 'Serviço',
  });

  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const adicionarFornecedor = (e) => {
    e.preventDefault();

    // Verifique se todos os campos foram preenchidos
    if (!novoFornecedor.nome || !novoFornecedor.descricao || !novoFornecedor.categoria) {
      alert('Por favor, preencha todos os campos.');
      return;
    }

    axios
      .post('http://localhost:5000/fornecedor', novoFornecedor, {
        headers: { 'Content-Type': 'application/json' },
      })
      .then((response) => {
        if (response.status === 201) {
          alert('Fornecedor adicionado com sucesso.');
          setNovoFornecedor({ nome: '', descricao: '', categoria: '' });
          onAdicionar(novoFornecedor);
          closeModal(); // Feche o modal após adicionar com sucesso
        } else {
          alert('Falha ao adicionar o fornecedor.');
        }
      })
      .catch((error) => {
        console.error(error);
        alert('Erro ao adicionar o fornecedor.');
      });
  };

  return (
    <div>
      <button className="ver-button" onClick={openModal}>
        Adicionar Fornecedor
      </button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Adicionar Fornecedor"
      >
        <div className="adicionar-fornecedor">
          <h2>Adicionar Novo Fornecedor</h2>
          <form onSubmit={adicionarFornecedor}>
            {/* Campos do formulário */}
            <button type="submit" className="editar-button">
              Adicionar
            </button>
            <button
              type="button"
              className="excluir-button"
              onClick={closeModal}
            >
              Cancelar
            </button>
          </form>
        </div>
      </Modal>
    </div>
  );
}

export default AdicionarFornecedor;
