  return (
    <div>
      <button onClick={() => setModalIsOpen(true)}>Endereços</button>
      <Modal
        // isOpen={modalIsOpen}
        isOpen={exibirEnderecos}
        // onRequestClose={() => setModalIsOpen(false)}
        onRequestClose={fecharModalEnderecos}
        contentLabel="Endereços do Fornecedor"
      >
        <h2>Endereços do Fornecedor</h2>
        <ul>
          {enderecos.map((endereco) => (
            <li key={endereco.fornecedorEndereco_id}>
              <strong>Tipo de Endereço:</strong> {endereco.tipo_endereco}<br />
              <strong>CEP:</strong> {endereco.cep}<br />
              <strong>Cidade:</strong> {endereco.cidade}<br />
              <strong>Endereço:</strong> {endereco.endereco}<br />
              <strong>UF:</strong> {endereco.uf}<br />
            </li>
          ))}
        </ul>
        <button onClick={() => setModalIsOpen(false)}>Fechar</button>
      </Modal>
    </div>
  );
}

export default EnderecosFornecedor;
