import React, { useState } from 'react';
import axios from 'axios'; // Certifique-se de importar axios se você ainda não o fez

function AdicionarFornecedor({ onAdicionar, onCancel }) {
    const [novoFornecedor, setNovoFornecedor] = useState({
        nome: '',
        descricao: '',
        categoria: '',
    });

    const adicionarFornecedor = (e) => {
        e.preventDefault(); // Evita o comportamento padrão do formulário

        // Verifica se todos os campos foram preenchidos
        if (!novoFornecedor.nome || !novoFornecedor.descricao || !novoFornecedor.categoria) {
            alert('Por favor, preencha todos os campos.');
            return;
        }

        axios
            .post('http://localhost:5000/fornecedor', novoFornecedor, {
                headers: { 'Content-Type': 'application/json' }
            })
            .then((response) => {
                if (response.status === 201) {
                    alert('Fornecedor adicionado com sucesso.');

                    // Limpa o formulário
                    setNovoFornecedor({ nome: '', descricao: '', categoria: '' });

                    // Chama a função de callback para atualizar a lista de fornecedores
                    onAdicionar(novoFornecedor);
                } else {
                    alert('Falha ao adicionar o fornecedor.');
                }
            })
            .catch((error) => {
                console.error(error);
                alert('Erro ao adicionar o fornecedor.');
            });
    };

    return (
        <div className="adicionar-fornecedor">
            <h2>Adicionar Novo Fornecedor</h2>
            <form onSubmit={adicionarFornecedor}>
                <div>
                    <label htmlFor="nome">Nome:</label>
                    <input
                        type="text"
                        id="nome"
                        className="input-estilizado"
                        value={novoFornecedor.nome}
                        onChange={(e) => setNovoFornecedor({ ...novoFornecedor, nome: e.target.value })}
                    />
                </div>
                <div>
                    <label htmlFor="descricao">Descrição:</label>
                    <input
                        type="text"
                        className="input-estilizado"
                        id="descricao"
                        value={novoFornecedor.descricao}
                        onChange={(e) => setNovoFornecedor({ ...novoFornecedor, descricao: e.target.value })}
                    />
                </div>
                <div>
                    <label htmlFor="categoria">Categoria:</label>
                    <select
                        value={novoFornecedor.categoria}
                        onChange={(e) => setFornecedorEditado({ ...novoFornecedor, categoria: e.target.value })}
                        className="dropdown">
                        <option value="Serviços">Serviços</option>
                        <option value="Consultoria">Consultoria</option>
                        <option value="Fabricante">Fabricante</option>
                    </select>
                </div>
                <button type="submit">Adicionar</button>
                <button type="button" onClick={onCancel}>Cancelar</button>
            </form>
        </div>
    );
}

export default AdicionarFornecedor;
