import { useEffect, useState } from "react"
import Item from "../components/Item"
import FornecedorEnderecos from "../components/FornecedorEnderecos"
import banner from '../assets/banner.png'
import axios from 'axios'

import '../pages/fornecedores.css'


export default function Products() {
    const [fornecedorList, setFornecedor] = useState([])

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/fornecedores')
            .then(res => setFornecedor(res.data.fornecedores))
            .catch(error => console.log(error))
    }, [])

    const [fornecedorEditado, setFornecedorEditado] = useState(null);
    const [editando, setEditando] = useState(false);

    const excluirOuEditarFornecedor = (fornecedorId) => {
        if (editando) {
        // Enviar uma solicitação PUT ou POST para atualizar o fornecedor
        axios
            .put(`http://127.0.0.1:5000/fornecedor?id=${fornecedorId}`, fornecedorEditado)
            .then((response) => {
            if (response.status === 200) {
                alert(`Fornecedor com ID ${fornecedorId} foi atualizado com sucesso.`);
                // Limpar o estado de edição
                setFornecedorEditado(null);
                setEditando(false);
            } else {
                alert(`Falha ao atualizar o fornecedor com ID ${fornecedorId}.`);
            }
            })
            .catch((error) => {
            console.error(error);
            alert(`Erro ao atualizar o fornecedor com ID ${fornecedorId}.`);
            });
        } else {
        // Aqui, você pode manter a lógica existente para exclusão de fornecedor
        axios
            .delete(`http://127.0.0.1:5000/fornecedor?id=${fornecedorId}`)
            .then((response) => {
            if (response.status === 200) {
                alert(`Fornecedor com ID ${fornecedorId} foi excluído com sucesso.`);
                // Atualize a lista de fornecedores após a exclusão
                setFornecedor(fornecedorList.filter((fornecedor) => fornecedor.id !== fornecedorId));
            } else {
                alert(`Falha ao excluir o fornecedor com ID ${fornecedorId}.`);
            }
            })
            .catch((error) => {
            console.error(error);
            alert(`Erro ao excluir o fornecedor com ID ${fornecedorId}.`);
            });
        }
    };

    return (
        <div className="content-product">
            <header>
                <div className="user">
                    <span>Usuário</span>
                </div>
            </header>

            <section className="banner">
                <img src={banner} alt="Banner" />
            </section>

            <section className="fornecedores-section">
                <h1>Lista de Fornecedores</h1>
                <table className="table-container">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Descrição</th>
                            <th>Categoria</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {fornecedorList.map((fornecedor) => (
                            <tr key={fornecedor.id}>
                                <td>{fornecedor.id}</td>
                                <td>{editando && fornecedor.id === fornecedorEditado?.id ? 
                                    <input type="text" 
                                           value={fornecedorEditado.nome} 
                                           onChange={(e) => setFornecedorEditado({ ...fornecedorEditado, nome: e.target.value })} /> : 
                                    fornecedor.nome}
                                </td>
                                <td>{editando && fornecedor.id === fornecedorEditado?.id ? <input type="text" value={fornecedorEditado.descricao} onChange={(e) => setFornecedorEditado({ ...fornecedorEditado, descricao: e.target.value })} /> : fornecedor.descricao}</td>
                                <td>{editando && fornecedor.id === fornecedorEditado?.id ? <input type="text" value={fornecedorEditado.categoria} onChange={(e) => setFornecedorEditado({ ...fornecedorEditado, categoria: e.target.value })} /> : fornecedor.categoria}</td>
                                <td>
                                    <button className="ver-button" onClick={console.log(fornecedor.id)}>
                                        Endereços
                                    </button>
                                    <button className="editar-button" onClick={() => setEditando(true)}>
                                        Editar
                                    </button>
                                    <button className="excluir-button" onClick={() => excluirOuEditarFornecedor(fornecedor.id)}>
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>

            <footer></footer>
        </div>
    )
}
