import { useState } from "react";

export default function Item(props) {
    // console.log(props)

    const [fornecedores, setFornecedores] = useState([
        // Substitua com os dados reais dos fornecedores
        { id: 1, nome: "Fornecedor 1", descricao: "Descrição 1", categoria: "Categoria A" },
        { id: 2, nome: "Fornecedor 2", descricao: "Descrição 2", categoria: "Categoria B" },
        // Adicione mais fornecedores conforme necessário
      ]);

      const [novoFornecedor, setNovoFornecedor] = useState({
        id: null,
        nome: "",
        descricao: "",
        categoria: "",
      });

      const [modoEdicao, setModoEdicao] = useState(false);

      const [fornecedorSelecionado, setFornecedorSelecionado] = useState(null);


    const [quantity, setQuantity] = useState(1)
    const [valueButton, setValueButton] = useState(true)
    const product = props.product
    console.log("asasass" + product.valor)

    // Função para subtrair o valor da quantidade de um produto
    const minus = () => {
        (quantity - 1) === 1 ? setValueButton(true) : setValueButton(false)
        if (quantity !== 1) {
            setQuantity(quantity - 1)
        }
    }


    // Função para somar valor da quantidade de um produto
    const plus = () => {
        setValueButton(false)
        setQuantity(quantity + 1)
    }

    // Função para comprar um produto informando o preço final
    const buyProduct = () => {
        if (window.confirm(`Preço final: ${Math.round((product.price * quantity) * 100) / 100}`)) {
            alert('Produto comprado com sucesso!')
            setQuantity(1)
        }
    }


    return (
        // <article className="product">
        //     <img src={product.imagem} alt="product"/>
        //     <h3 className="price-product">
        //         R$ <span>{product.valor}</span>
        //     </h3>
        //     <p className="name-product">{product.nome}</p>
        //     <div className="quantity">
        //         <span >Quantidade:</span>
        //         <button disabled={valueButton} className="minus" onClick={minus}>-</button>
        //         <span>{quantity}</span>
        //         <button className="plus" onClick={plus}>+</button>
        //     </div>
        //     <button className="buy" onClick={buyProduct}>Comprar</button>
        // </article>
        <div>
            <h1>Lista de Fornecedores</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Descrição</th>
                        <th>Categoria</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {carregarFornecedores()}
                    {modoEdicao && (
                        <tr>
                            <td>{novoFornecedor.id}</td>
                            <td>
                                <input
                                    type="text"
                                    value={novoFornecedor.nome}
                                    onChange={(e) => setNovoFornecedor({ ...novoFornecedor, nome: e.target.value })}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={novoFornecedor.descricao}
                                    onChange={(e) => setNovoFornecedor({ ...novoFornecedor, descricao: e.target.value })}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={novoFornecedor.categoria}
                                    onChange={(e) => setNovoFornecedor({ ...novoFornecedor, categoria: e.target.value })}
                                />
                            </td>
                            <td>
                                <button onClick={salvarEdicao}>Salvar</button>
                                <button onClick={cancelarEdicao}>Cancelar</button>
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
            <button onClick={adicionarFornecedor}>Adicionar Fornecedor</button>
        </div>
    );
}
