import { useEffect, useState } from "react"
import Item from "../components/Item"
import FornecedorEnderecos from "../components/FornecedorEnderecos"
import banner from '../assets/banner.png'
import axios from 'axios'

import '../pages/fornecedores.css'

export default function Products() {
    const [fornecedorList, setFornecedor] = useState([])
    const [id, setId] = useState(0)
    const [del, setDel] = useState(false)

    const setExcluir = (idFor) => {
        //setId(idFor)
        //setDel(true)
    } 

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/fornecedores')
            .then(res => setFornecedor(res.data.fornecedores))
            .catch(error => console.log(error))
    }, [])

    useEffect(() => {
        axios
          .delete(`http://127.0.0.1:5000/fornecedor?id=${id}`)
          .then((response) => {
            if (response.status === 200) {
              alert(`Fornecedor com ID ${id} foi excluído com sucesso.`);
              // Atualize a lista de fornecedores após a exclusão
              setFornecedor(fornecedorList.filter((fornecedor) => fornecedor.id !== id));
            } else {
              alert(`Falha ao excluir o fornecedor com ID ${id}.`);
            }
          })
          .catch((error) => {
            console.error(error);
            alert(`Erro ao excluir o fornecedor com ID ${id}.`);
          });
          setDel(false)
    }, [del])

    return (
        <div className="content-product">
            <header>
                <div className="user">
                    <span>Usuário</span>
                </div>
            </header>

            <section className="banner">
                <img src={banner} alt="Banner" />
            </section>

            <section className="fornecedores-section">
                <h1>Lista de Fornecedores</h1>
                <table className="table-container">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Descrição</th>
                            <th>Categoria</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {fornecedorList.map((fornecedor) => (
                            <tr key={fornecedor.id}>
                                <td>{fornecedor.id}</td>
                                <td>{fornecedor.nome}</td>
                                <td>{fornecedor.descricao}</td>
                                <td>{fornecedor.categoria}</td>
                                <td>
                                    {/* <button onClick={salvarEdicao}>Salvar</button> */}
                                    <button onClick={() => setId(fornecedor.id)}>Exclui</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>

            <footer></footer>
        </div>
    )
}
